@import url("https://fonts.googleapis.com/css?family=Lato|Schoolbell");
@import url("https://fonts.googleapis.com/css?family=Crimson+Text");
@import url("https://fonts.googleapis.com/css?family=Lato");
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box; }

ul {
  list-style-type: none; }

body {
  font-family: 'Lato', 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
  font-weight: 400;
  font-size: 16px;
  line-height: 1.7;
  color: #777;
  padding: 30px; }

.container {
  display: grid;
  grid-template-columns: repeat(12, 1fr);
  grid-gap: 1em; }

.header {
  height: 95vh;
  min-width: 580px;
  margin-bottom: 1em;
  background-image: linear-gradient(to right bottom, rgba(45, 180, 210, 0.8), rgba(10, 50, 55, 0.8)), url(ec781c90558910beecdfddac3b760333.jpg);
  clip-path: polygon(0 0, 100% 0, 100% 75vh, 0 100%);
  background-size: cover;
  background-position: top;
  position: relative;
  grid-column: 1 / -1; }

.card-header-title {
  font-size: 2em;
  font-weight: 700;
  font-family: 'Crimson Text', 'Courier New', Courier, monospace;
  display: inline; }

.logo-box {
  position: absolute;
  top: 20px;
  left: 20px; }

.logo {
  height: 75px; }

.text-box {
  position: absolute;
  top: 40%;
  left: 50%;
  transform: translate(-50%, -50%); }

.heading-primary {
  color: #fff;
  text-transform: uppercase;
  backface-visibility: hidden; }

.heading-primary-main {
  font-size: 1.5em;
  display: block;
  font-weight: 700;
  letter-spacing: 5px;
  animation: moveInLeft 1s ease-in-out; }

.heading-primary-sub {
  font-size: 1em;
  font-weight: 400;
  letter-spacing: 10px;
  display: block;
  animation: moveInRight 1s ease-in-out; }

@keyframes moveInLeft {
  0% {
    opacity: 0;
    transform: translateX(-100px); }
  80% {
    transform: translateX(10px); }
  100% {
    opacity: 1;
    transform: translateX(0); } }

@keyframes moveInRight {
  0% {
    opacity: 0;
    transform: translateX(100px); }
  80% {
    transform: translateX(-10px); }
  100% {
    opacity: 1;
    transform: translateX(0); } }

.menu {
  grid-column: 1/4;
  border: 1px solid #30b6d1; }

.menu-header {
  font-family: 'Crimson Text', 'Courier New', Courier, monospace;
  font-weight: 400;
  background-color: #30b6d1;
  color: white;
  font-size: 0.8em;
  text-align: center;
  height: 3em; }

.menu a {
  color: #30b6d1;
  text-decoration: none;
  margin: 1em; }

.main {
  grid-column: 4/-1; }

.main-section {
  border: 1px solid #30b6d1;
  margin-bottom: 1rem; }

.section-header {
  background-color: #30b6d1;
  color: white;
  font-size: 2em;
  font-family: 'Crimson Text', 'Courier New', Courier, monospace;
  padding-left: 1rem;
  text-align: center; }

.section-header-icon {
  float: right;
  padding-right: 1rem; }

.section-header a {
  color: white; }

.section-content {
  padding: 1rem; }
