generated.css:
@import url("https://fonts.googleapis.com/css?family=Lato|Schoolbell");
@import url("https://fonts.googleapis.com/css?family=Crimson+Text");
@import url("https://fonts.googleapis.com/css?family=Lato");
@keyframes moveInRight {
  0% {
    opacity: 0;
    transform: translateX(100px); }
  80% {
    transform: translateX(-10px); }
  100% {
    opacity: 1;
    transform: translateX(0); } }

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box; }

html {
  scroll-behavior: smooth; }

body {
  display: grid;
  grid-template-columns: min-content 1fr;
  font-family: 'Lato', 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif; }

header {
  grid-column: 1 / 3;
  background: #30b6d1;
  color: white;
  padding: 4rem;
  text-align: center; }

nav {
  background: #196e82; }

nav ul {
  list-style: none; }

nav ul li a {
  display: block;
  padding: 0.5rem 1rem;
  color: white;
  text-decoration: none; }

nav ul li a {
  display: block;
  padding: 0.5rem 1rem;
  text-decoration: none; }

nav ul li a.current {
  background: black; }

main {
  padding-bottom: 40rem; }

section {
  padding: 2rem;
  margin: 0 0 2rem 0; }

footer {
  grid-column: 1 /3;
  background: #30b6d1;
  padding: 5rem 1rem; }

@media (min-height: 300px) {
  nav ul {
    position: sticky;
    top: 0; } }
