@import url("https://fonts.googleapis.com/css?family=Lato|Schoolbell");
@import url("https://fonts.googleapis.com/css?family=Crimson+Text");
@import url("https://fonts.googleapis.com/css?family=Lato");
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box; }

body {
  font-family: 'Lato', 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
  font-weight: 400;
  font-size: 16px;
  line-height: 1.7;
  color: #777;
  padding: 30px; }

.header {
  height: 95vh;
  min-width: 580px;
  background-image: linear-gradient(to right bottom, rgba(45, 180, 210, 0.5), rgba(10, 50, 55, 0.5)), url(ec781c90558910beecdfddac3b760333.jpg);
  clip-path: polygon(0 0, 100% 0, 100% 75vh, 0 100%);
  background-size: cover;
  background-position: top;
  position: relative; }

.logo-box {
  position: absolute;
  top: 20px;
  left: 20px; }

.logo {
  height: 75px; }

.text-box {
  position: absolute;
  top: 40%;
  left: 50%;
  transform: translate(-50%, -50%); }

.heading-primary {
  color: #fff;
  text-transform: uppercase; }

.heading-primary-main {
  font-size: 25px;
  display: block;
  font-weight: 700;
  letter-spacing: 19px; }

.heading-primary-sub {
  font-size: 40px;
  font-weight: 400;
  letter-spacing: 5px;
  display: block; }

@media screen and (max-width: 1280px) {
  .heading-primary-sub {
    font-size: 24px;
    font-weight: 400;
    display: block; } }
