@import url("https://fonts.googleapis.com/css?family=Lato|Schoolbell");
@import url("https://fonts.googleapis.com/css?family=Crimson+Text");
@import url("https://fonts.googleapis.com/css?family=Lato");
/* http://meyerweb.com/eric/tools/css/reset/ 
   v2.0 | 20110126
   License: none (public domain)
*/
html, body, div, span, applet, object, iframe,
h1, h2, h3, h4, h5, h6, p, blockquote, pre,
a, abbr, acronym, address, big, cite, code,
del, dfn, em, img, ins, kbd, q, s, samp,
small, strike, strong, sub, sup, tt, var,
b, u, i, center,
dl, dt, dd, ol, ul, li,
fieldset, form, label, legend,
table, caption, tbody, tfoot, thead, tr, th, td,
article, aside, canvas, details, embed,
figure, figcaption, footer, header, hgroup,
menu, nav, output, ruby, section, summary,
time, mark, audio, video {
  margin: 0;
  padding: 0;
  border: 0;
  font-size: 100%;
  font: inherit;
  vertical-align: baseline; }

/* HTML5 display-role reset for older browsers */
article, aside, details, figcaption, figure,
footer, header, hgroup, menu, nav, section {
  display: block; }

body {
  line-height: 1; }

ol, ul {
  list-style: none; }

blockquote, q {
  quotes: none; }

blockquote:before, blockquote:after,
q:before, q:after {
  content: '';
  content: none; }

table {
  border-collapse: collapse;
  border-spacing: 0; }

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box; }

body {
  font-family: 'Lato', 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
  font-weight: 400;
  font-size: 16px;
  line-height: 1.7;
  color: #777;
  padding: 30px; }

.card-header-title {
  font-size: 2em;
  font-weight: 700;
  font-family: 'Crimson Text';
  display: inline; }

.header {
  height: 95vh;
  min-width: 580px;
  background-image: linear-gradient(to right bottom, rgba(45, 180, 210, 0.8), rgba(10, 50, 55, 0.8)), url(ec781c90558910beecdfddac3b760333.jpg);
  clip-path: polygon(0 0, 100% 0, 100% 75vh, 0 100%);
  background-size: cover;
  background-position: top;
  position: relative; }

.logo-box {
  position: absolute;
  top: 20px;
  left: 20px; }

.logo {
  height: 75px; }

.text-box {
  position: absolute;
  top: 40%;
  left: 50%;
  transform: translate(-50%, -50%); }

.heading-primary {
  color: #fff;
  text-transform: uppercase;
  backface-visibility: hidden; }

.heading-primary-main {
  font-size: 1.5em;
  display: block;
  font-weight: 700;
  letter-spacing: 5px;
  animation: moveInLeft 1s ease-in-out; }

.heading-primary-sub {
  font-size: 1em;
  font-weight: 400;
  letter-spacing: 10px;
  display: block;
  animation: moveInRight 1s ease-in-out; }

@keyframes moveInLeft {
  0% {
    opacity: 0;
    transform: translateX(-100px); }
  80% {
    transform: translateX(10px); }
  100% {
    opacity: 1;
    transform: translateX(0); } }

@keyframes moveInRight {
  0% {
    opacity: 0;
    transform: translateX(100px); }
  80% {
    transform: translateX(-10px); }
  100% {
    opacity: 1;
    transform: translateX(0); } }

a {
  color: inherit; }

a:hover {
  color: #30B6D1; }

.card {
  margin-top: 1em;
  padding: 1em;
  min-width: 400px;
  border: 1px solid #30B6D1; }

.card-header-icon {
  color: #30B6D1; }

.menu {
  background-color: #30B6D1; }

.menu ul {
  list-style-type: none;
  padding: 0; }

.menu a {
  text-decoration: none;
  text-align: center;
  color: #fff;
  display: block;
  padding: 10px; }

.menu a:hover {
  background-color: #196e82; }

